INSERT INTO `cache_field` VALUES ('field:node:20','a:1:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:2279:\"<div class=\"span6 contentsubrow contentsubrow-l\">\r\n                    <div class=\"span6 homecon homecon-top\">\r\n                        <div class=\"span2 homecon-topsec homecon-topsec-a\">\r\n                                  <img src=\"/sites/all/themes/bootstrap/mega/img/body/smiler.png\" alt=\"smile\" height=\"108\" width=\"140\" />\r\n                        </div>\r\n                        <div class=\"span4 homecon-topsec homecon-topsec-b\">\r\n                            <h3>Mega Minnesota</h3>\r\n                            <h4>Welcome to our Portal</h4>\r\n                <span>We are a non-profit and non-trading organization devoted to its member\'s welfare.This Association is not a political one nor is it to be associated or affiliated to any political organization or party.\r\n                </span>\r\n                        </div>\r\n                    </div>\r\n                    <div class=\"span6 homecon homecon-bot\">\r\n                        <span class=\"xlead\">Our key objectives are:</span> </br>\r\n                        <ul>\r\n                            <li>\r\n                                a) To promote unity and solidarity of the members and other organizations with similar goals, eg: the church.\r\n                            </li>\r\n                            <li>\r\n                                b) To do and carry out those objectives that are generally and naturally related to the main objectives.\r\n                            </li>\r\n                            <li>\r\n                                c) To support members both financially and emotionally in the event of loss of a member or a close relative.\r\n                            </li>\r\n                        </ul>\r\n                    </div>\r\n                </div>\r\n                <div class=\"span3 contentsubrow contentsubrow-r\">\r\n                    <h3>Our Mission</h3>\r\n                    Our mission is to promote unity, solidarity and cultural practices and to support our members in social issues and total well being.\r\n\r\n                    Mega Minnesota is a non- Profit and non-trading organization to be devoted to its member\'s welfare.\r\n                    It is non-political and is not to be associated or affiliated with any political party or organization.\r\n                </div>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:1862:\"<div class=\"span6 contentsubrow contentsubrow-l\">\n<div class=\"span6 homecon homecon-top\">\n<div class=\"span2 homecon-topsec homecon-topsec-a\">\n                                  <img src=\"/sites/all/themes/bootstrap/mega/img/body/smiler.png\" alt=\"smile\" height=\"108\" width=\"140\" /></div>\n<div class=\"span4 homecon-topsec homecon-topsec-b\">\n<h3>Mega Minnesota</h3>\n<h4>Welcome to our Portal</h4>\n<p>                <span>We are a non-profit and non-trading organization devoted to its member\'s welfare.This Association is not a political one nor is it to be associated or affiliated to any political organization or party.<br /></span>\n                        </p></div>\n</div>\n<div class=\"span6 homecon homecon-bot\">\n                        <span class=\"xlead\">Our key objectives are:</span> \n<ul><li>\n                                a) To promote unity and solidarity of the members and other organizations with similar goals, eg: the church.\n                            </li>\n<li>\n                                b) To do and carry out those objectives that are generally and naturally related to the main objectives.\n                            </li>\n<li>\n                                c) To support members both financially and emotionally in the event of loss of a member or a close relative.\n                            </li>\n</ul></div>\n</div>\n<div class=\"span3 contentsubrow contentsubrow-r\">\n<h3>Our Mission</h3>\n<p>                    Our mission is to promote unity, solidarity and cultural practices and to support our members in social issues and total well being.</p>\n<p>                    Mega Minnesota is a non- Profit and non-trading organization to be devoted to its member\'s welfare.<br />\n                    It is non-political and is not to be associated or affiliated with any political party or organization.\n                </p></div>\n\";s:12:\"safe_summary\";s:0:\"\";}}}}',0,1383147520,1),('field:node:23','a:1:{s:4:\"body\";a:1:{s:3:\"und\";a:1:{i:0;a:5:{s:5:\"value\";s:30:\"<h4>We Would Like To Hear</h4>\";s:7:\"summary\";s:0:\"\";s:6:\"format\";s:9:\"full_html\";s:10:\"safe_value\";s:31:\"<h4>We Would Like To Hear</h4>\n\";s:12:\"safe_summary\";s:0:\"\";}}}}',0,1383145316,1),('field_info:bundle:comment:comment_node_article','a:2:{s:9:\"instances\";a:1:{s:12:\"comment_body\";a:13:{s:5:\"label\";s:7:\"Comment\";s:8:\"settings\";a:2:{s:15:\"text_processing\";i:1;s:18:\"user_register_form\";b:0;}s:8:\"required\";b:1;s:7:\"display\";a:1:{s:7:\"default\";a:5:{s:5:\"label\";s:6:\"hidden\";s:4:\"type\";s:12:\"text_default\";s:6:\"weight\";i:0;s:8:\"settings\";a:0:{}s:6:\"module\";s:4:\"text\";}}s:6:\"widget\";a:4:{s:4:\"type\";s:13:\"text_textarea\";s:8:\"settings\";a:1:{s:4:\"rows\";i:5;}s:6:\"weight\";i:0;s:6:\"module\";s:4:\"text\";}s:11:\"description\";s:0:\"\";s:2:\"id\";s:1:\"3\";s:8:\"field_id\";s:1:\"1\";s:10:\"field_name\";s:12:\"comment_body\";s:11:\"entity_type\";s:7:\"comment\";s:6:\"bundle\";s:20:\"comment_node_article\";s:7:\"deleted\";s:1:\"0\";s:13:\"default_value\";N;}}s:6:\"fields\";a:1:{i:0;a:16:{s:12:\"entity_types\";a:1:{i:0;s:7:\"comment\";}s:12:\"translatable\";s:1:\"0\";s:8:\"settings\";a:0:{}s:7:\"storage\";a:5:{s:4:\"type\";s:17:\"field_sql_storage\";s:8:\"settings\";a:0:{}s:6:\"module\";s:17:\"field_sql_storage\";s:6:\"active\";s:1:\"1\";s:7:\"details\";a:1:{s:3:\"sql\";a:2:{s:18:\"FIELD