INSERT INTO `field_data_body` VALUES ('node','page',0,2,2,'und',0,'<div class=\"divx span8\">\r\n        <legend>About us</legend>\r\n        <p>Mega Minnesota is a non- Profit and non-trading organization to be devoted to its member\'s welfare. It is non-political and is not to be associated or affiliated with any political party or organization.</p\r\n</div>\r\n<div class=\"divx span8\"><h4>Mission statement </h4>\r\n<p>Our mission is to promote unity, solidarity and cultural practices and to support members in social issues and total well being.</p>\r\n</div>','','full_html'),('node','page',0,3,3,'und',0,'<form name=\"_xclick\" action=\"https://www.sandbox.paypal.com/cgi-bin/webscr\" method=\"post\">\r\n        <div class=\"regsec regsec-body span8\">\r\n    	<fieldset>\r\n            <legend>Support Mega Minnesota</legend>\r\n            <div class=\"reg-row span8\">\r\n                <div class=\"reg-row-col reg-row-col-a center-paypal\">\r\n                       <span>Amount:</span><span class=\"add-on\">$</span><input type=\"text\" class=\"inputtext\" size=\"30\" id=\"paypalAmount\" name=\"amount\"/>\r\n                </div>            \r\n            </div><input type=\"hidden\" name=\"cmd\" value=\"_xclick\"><input type=\"hidden\" name=\"business\" value=\"megaminnesota@gmail.com\"><input type=\"hidden\" name=\"currency_code\" value=\"USD\"><input type=\"hidden\" name=\"item_name\" value=\"MegaMN Donation:2013-10-26\"><input name=\"item_number\" type=\"hidden\" value=\"MegaMN Donation:2013-10-26\" />\r\n</fieldset>\r\n        </div>\r\n    	<div class=\"reg-row span8 reg-row-submit\">\r\n              <div class=\"reg-row-col reg-row-col-a span3\">\r\n                  <div></div>\r\n              </div>        \r\n              <div class=\"reg-row-col reg-row-col-b reg-row-col-b-b span3\">\r\n                    <input type=\"image\" src=\"http://www.paypal.com/en_US/i/btn/btn_donate_LG.gif\" border=\"0\" name=\"submit\" alt=\"Make payments with PayPal - it\'s fast, free and secure!\" id=\"paypalButton\" />\r\n              </div>\r\n      	</div>\r\n</form>','','full_html'),('node','home',0,20,20,'und',0,'<div class=\"span6 contentsubrow contentsubrow-l\">\r\n                    <div class=\"span6 homecon homecon-top\">\r\n                        <div class=\"span2 homecon-topsec homecon-topsec-a\">\r\n                                  <img src=\"/sites/all/themes/bootstrap/mega/img/body/smiler.png\" alt=\"smile\" height=\"108\" width=\"140\" />\r\n                        </div>\r\n                        <div class=\"span4 homecon-topsec homecon-topsec-b\">\r\n                            <h3>Mega Minnesota</h3>\r\n                            <h4>Welcome to our Portal</h4>\r\n                <span>We are a non-profit and non-trading organization devoted to its member\'s welfare.This Association is not a political one nor is it to be associated or affiliated to any political organization or party.\r\n                </span>\r\n                        </div>\r\n                    </div>\r\n                    <div class=\"span6 homecon homecon-bot\">\r\n                        <span class=\"xlead\">Our key objectives are:</span> </br>\r\n                        <ul>\r\n                            <li>\r\n                                a) To promote unity and solidarity of the members and other organizations with similar goals, eg: the church.\r\n                            </li>\r\n                            <li>\r\n                                b) To do and carry out those objectives that are generally and naturally related to the main objectives.\r\n                            </li>\r\n                            <li>\r\n                                c) To support members both financially and emotionally in the event of loss of a member or a close relative.\r\n                            </li>\r\n                        </ul>\r\n                    </div>\r\n                </div>\r\n                <div class=\"span3 contentsubrow contentsubrow-r\">\r\n                    <h3>Our Mission</h3>\r\n                    Our mission is to promote unity, solidarity and cultural practices and to support our members in social issues and total well being.\r\n\r\n                    Mega Minnesota is a non- Profit and non-trading organization to be devoted to its member\'s welfare.\r\n                    It is non-political and is not to be associated or affiliated with any political party or organization.\r\n                </div>','','full_html'),('node','policy',0,22,22,'und',0,'This is where all members information will be laid out','','filtered_html'),('node','contact',0,23,23,'und',0,'<h4>We Would Like To Hear</h4>','','full_html');
